mapper.update:
update bbs
set bbs_praise=bbs_praise + 1
where bbs_id = #{bbsId}

update bbs
set bbs_praise=bbs_praise - 1
where bbs_id = #{bbsId}

update bbs
set bbs_no=bbs_no + 1
where bbs_id = #{bbsId}

update bbs
set bbs_no=bbs_no - 1
where bbs_id = #{bbsId}

update bbs
set bbs_context=#{bbsContext},
    bbs_context_md=#{bbsContextMd},
    bbs_title=#{bbsTitle}
where bbs_id = #{bbsId}

mapper.select:
select bbs_user_id
from bbs
where bbs_id = #{bbsId}
